#!/bin/bash
echo "######################################################################################################################################################"
echo "#---------------------------------------------------------ip info------------------------------------------------------------------------------------#"
echo "######################################################################################################################################################"
ip -o a | grep bond | grep -v inet6
echo -e "\n######################################################################################################################################################"
echo "#--------------------------------------------------------inet info-----------------------------------------------------------------------------------#"
echo "######################################################################################################################################################"
ip -o l  | grep -E '(em[0-9]|p[0-9]p[0-9])'
echo -e "\n######################################################################################################################################################"
echo "#--------------------------------------------------------bond info-----------------------------------------------------------------------------------#"
echo "######################################################################################################################################################"
echo -e "\n----------------------------------------------------------------bond conf----------------------------------------------------------------------------------"
	cat /etc/modprobe.d/bond.conf
for net in  `ls  /proc/net/bonding`;do
	echo -e "\n---------------------------------------------------------$net---------------------------------------------------------------------------------------"
	cat /proc/net/bonding/$net
done
